import React, {useState} from 'react';
import { Link as ReactRouterLink } from 'react-router-dom';

import { Background,ButtonLink,
    Container,
    Logo, Group,Profile, Dropdown,
    Picture, Feature, Text, FeatureCallOut, 
    Search, SearchIcon, SearchInput,
    PlayButton,
    Link} from './styles/header';

export default function Header({ bg = true, children, ...restProps }) {
    return bg ? <Background { ...restProps}>{children}</Background> 
    : children; 
}

Header.Feature = function HeaderFeature({ children, ...restProps})  {
    return <Feature { ...restProps}>{children}</Feature>
};

Header.FeatureCallOut = function HeaderFeatureCallOut({ children, ...restProps})  {
    return <FeatureCallOut { ...restProps}>{children}</FeatureCallOut>
};

Header.Profile = function HeaderProfile({ children, ...restProps})  {
    return <Profile { ...restProps}>{children}</Profile>
};

Header.Picture = function HeaderPicture({ src, ...restprops}) {
    return <Picture {...restprops} src={`/images/users/${src}.png`}/>
}

Header.Search = function HeaderSearch({ searchTerm, setSearchTerm, ...restProps}) {
    const [searchActive, setSearchActive] = useState(false);

    return (
        <Search {...restProps}>
            <SearchIcon onClick={() => setSearchActive(searchActive => !searchActive)}>
                <img src="/images/icons/search.png" alt="Search" />
            </SearchIcon>
            <SearchInput vlaue={searchTerm} onChange={({ target}) => setSearchTerm(target.value)}
                placeholder="Search files and series"
                active={searchActive}
            />
        </Search>
    )
}

Header.Dropdown = function HeaderDropdown({ children, ...restProps})  {
    return <Dropdown { ...restProps}>{children}</Dropdown>
};

Header.Text = function HeaderText({ children, ...restProps})  {
    return <Text { ...restProps}>{children}</Text>
};

Header.TextLink = function HeaderTextLink({ children, ...restProps})  {
    return <Link { ...restProps}>{children}</Link>
};

Header.PlayButton = function HeaderPlayButton({children, ...restprops}) {
    return <PlayButton {...restprops}>{children}</PlayButton>
}

Header.Frame = function HeaderFrame({ children, ...restProps})  {
    return <Container { ...restProps}>{children}</Container>
};

Header.Group = function HeaderGroup({ children, ...restProps})  {
    return <Group { ...restProps}>{children}</Group>
};

Header.ButtonLink = function HeaderButtonLink({ children, ...restProps})  {
    return <ButtonLink { ...restProps}>{children}</ButtonLink>
};




Header.Logo = function HeaderLogo({ to, ...restProps }) {
    return (
        <ReactRouterLink to={to}>
            <Logo {...restProps} />
        </ReactRouterLink>
    );
};
