import styled from 'styled-components/macro';

export const cardsrow = styled.div`
    display: flex;
`;

export const card2 = styled.div `
    display: flex;
    
    padding: 5px 10px;
`

export const cardImage = styled.div`
    display: flex;
    color: white;
`

export const ContentBlock = styled.div`
    display: flex;
    position: relative;
    flex: calc(18vw - 4px);
    flex-shrink: 0;
    height: 9.5vw;
    margin-right: 4px;

    img {
        height: 100%;
        width: 100%;
        pointer-events: none;
        
    }
    
`
