import styled from 'styled-components/macro';
import { Link as ReactRouterLink } from 'react-router-dom';

export const Background = styled.div`
    display: flex;
    flex-direction: column;
    background: url('../images/misc/home-bg.jpg');
`;

export const Frame = styled.div``;

export const Container = styled.div`
    display: flex;
    margin: 0, 56px;
    height: 64px;
    padding: 18px 25px;
    justify-content: space-between;
    align-items: center;
`;

export const Logo = styled.img`
    height: 32px;
    width: 100px;
    margin-right: 40px;

    @media (max-width: 1000px) {
        height: 45px;
        width: 167px;
    }

`;

export const ButtonLink = styled(ReactRouterLink)`
    display: block;
    background-color: #e50914;
    width: 100px;
    height: 35px;
    padding-top: 7px;
    color: white;
    border: 0;
    font-size: 20px;
    border-radius: 7px;
    cursor: pointer;
    text-decoration: none;
    
    text-align: center;

    &:hover {
        background-color: white;
        color: red;
    }
`;

export const ButtonLinktwo = styled(ReactRouterLink)`
    
    background-color: #e50914;
    width: 100px;
    height: 15px;
    margin: 15px 23px;
    padding-top: 7px;
    color: white;
    border: 0;
    font-size: 20px;
    border-radius: 7px;
    cursor: pointer;
    text-decoration: none;

    text-align: center;

    &:hover {
        background-color: white;
        color: red;
    }

`;
