import styled from 'styled-components/macro';

export const Container = styled.div`
    display: flex;
    justify-content: center;
    height: 100%;
    margin-top: 20px;
    flex-wrap: wrap;
    padding-left: 20px;

    @media (max-width: 1000px) {
        flex-direction: column;
        align-items: center;
        
    }
`;

export const Button = styled.button`
    display: flex;
    align-items: center;
    height: 50px;
    background: #e50914;
    color: white;
    text-transform: uppercase;
    padding: 0 42px;
    font-size: 22px;
    border-radius: 10px;
    border: 0;
    cursor: pointer; 

    



`;

export const Text = styled.p`
    color: white;
    align-items: center;
    
`;
